import { Component } from '@angular/core';
import {products } from "../products"
import { LikeServiceService } from '../like-service.service';
@Component({
  selector: 'app-likes',
  templateUrl: './likes.component.html',
  styleUrls: ['./likes.component.scss']
})
export class LikesComponent {
  products= products;
  items = this.likeServiceService.getItems();
  constructor(
    private likeServiceService:LikeServiceService,
  ){}
}
<div class="cart-item" *ngFor="let item of items">
    <div class="image-container">
        <img src={{item.image}} alt="item-image">
    </div>
  <div class = "name-container">{{ item.name }}</div>
  <div class ="price-container">{{ item.price | currency }}</div>
</div>
