import { Component, OnInit } from '@angular/core';
import { Product,products } from '../products';
import { CartService } from '../cart.service';
import { ActivatedRoute } from '@angular/router';
import { LikeServiceService } from '../like-service.service';
import { ProductListComponent } from '../product-list/product-list.component';
@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
  styleUrls: ['./home.component.scss']
})
export class HomeComponent implements OnInit{
  product : Product |undefined;
  products=products;

  constructor(
    private route: ActivatedRoute,
    private cartService: CartService,
  ) {}
  ngOnInit(){
    const routeParams = this.route.snapshot.paramMap;
    const productCategorieFromRoute = String(routeParams.get('productCategorie'));
}
}
<div class="home-container" *ngFor="let product of products">
    <div class="container">
        <h1>TechStore</h1>
        <div class="slider-container">
        </div>
        <div class="categories-container">
            <div class="categories">
                <img src="https://images.macrumors.com/t/Au-OUIb73hHvx2w8CirAir5bNbM=/1600x/article-new/2013/09/macbook-air-m2-roundup-header.png" alt="f">
                <button> Laptops </button>
            </div>
            <div class="categories">
                <img src="https://m.media-amazon.com/images/I/61cwywLZR-L._AC_SX466_.jpg" alt="">                
                <button *ngIf="product.categorie"="Smartphones"
                    routerLink='/product-list'>Smartphones</button>
            </div>
            <div class="categories">
                <img src="" alt="">
                <button> Tablets</button>
            </div>
            <div class="categories">
                <img src="" alt="">
                <button>Browse all products</button>
            </div>
        </div>

    </div>
</div>
<button routerLink="/product-list">all products</button>
